public decimal CalculateShippingCost(decimal weight, int distance)
{
    decimal baseRate;
    if (weight <= 5)
        baseRate = 10.00m;
    else if (weight <= 20)
        baseRate = 15.00m;
    else
        baseRate = 20.00m;
    decimal distanceFactor = distance / 100m;
    return baseRate + (baseRate * distanceFactor);
}
