public decimal CalculateDiscount(decimal orderTotal, string customerType)
{
    if (customerType == "VIP")
    {
        return orderTotal * 0.15m;
    }
    else if (customerType == "Regular")
    {
        return orderTotal * 0.05m;
    }
    else
    {
        return 0m;
    }
}

// Usage
