public static Record[] QuickSort(Record[] records)
{
    if (records.Length <= 1) return records;
    var pivot = records[0];
    var left = new List<Record>();
    var right = new List<Record>();
    for (int i = 1; i < records.Length; ++i)
        if (records[i].Age < pivot.Age) left.Add(records[i]);
        else right.Add(records[i]);
    return QuickSort(left.Concat(new Record[] { pivot }).Concat(right).ToArray());
