        {
            if (balance >= amount)
            {
                balance -= amount;
                Console.WriteLine("Withdrew " + amount);
                return true;
            }
            else
            {
                throw new Exception("Insufficient funds.");
            }
        }
        else
        {
            throw new Exception("Incorrect password.");
        }
    }

