```csharp
public IList<Product> ParseAndConvertSqlSelect(string sql)
{
    var dbContextOptionsBuilder = new DbContextOptionsBuilder<MyDbContext>();
    using (var context = new MyDbContext(dbContextOptionsBuilder.Options))
    {
        // Split the SQL query into separate lines
        string[] lines = sql.Split('\n');
        // Find the SELECT statement and parse it to find the required properties and table
        var selectStatementLine = lines[1];
        var columns = selectStatementLine[9..].Split(',').Select(col => col.Trim()).ToList();
        var tableName = selectStatementLine[0..8];
        // Find the WHERE clause and parse the condition if present
        var whereClauseIndex = lines.FindIndex(line => line.StartsWith("WHERE"));
        string? whereCondition = null;
        if (whereClauseIndex != -1)
        {
            whereCondition = lines[whereClauseIndex + 1];
        }
        // Create an Entity Framework Core query using the parsed information
        var result = context.Set<Product>()
            .Select(p => new Product { Id = p.Id, Name = p.Name })
            .WhereIfNotNull(whereCondition, wc => wc);
        if (columns.Any())
        {
            result = result.OrderByDescendingIf(columns[0], "Price");
        }
        if (columns.Count > 1)
        {
            var columnIndex = 1;
            for (int i = 2; i < columns.Count; i++)
            {
                result = result.ThenByIf(columns[i], j => j.[columnName]);
            }
        }
        return await result.Take(10).ToListAsync();
    }
}
```

