{
    int[,] matrix = new int[s1.Length + 1, s2.Length + 1];

    for (int i = 0; i <= s1.Length; ++i)
    {
        for (int j = 0; j <= s2.Length; ++j)
        {
            if (i == 0 || j == 0)
