procedure bar_chart() = begin
    define binWidth as INTEGER(4) default 5000; -- $5,000 bins
    define binStart as DECIMAL(10,2); -- start of the first bin (in thousands)
    define bins as ARRAY_TYPE(INTEGER); -- array to store counts in each bin
    define numBins as INTEGER;

    for i = 1 to round((max(salary) - min(salary)) / binWidth) + 1 do begin
        set binStart = round((i-1) * binWidth) / 1000.0; -- convert bin index to thousands
        append (bins, count(*) where salary >= binStart and salary < binStart + binWidth);
